// +build avx
// +build linux,amd64

package gadk

import (
	"testing"
	"time"
)

func testPowAVX(t *testing.T) float64 {
	var tx Trytes = ""
	s := time.Now()
	nonce, err := PowAVX(tx, 15)
	ti := time.Now().Sub(s)
	sp := float64(countAVX) / 1000 / ti.Seconds()
	if err != nil {
		t.Fatal(err)
	}
	tx = tx[:len(tx)-NonceTrinarySize/3] + nonce
	h := tx.Hash()
	if h[len(h)-5:] != "99999" {
		t.Error("pow is illegal", h)
	}
	return sp
}

func TestPowAVX(t *testing.T) {
	sp := testPowAVX(t)
	t.Logf("%d kH/sec on AVX PoW", int(sp))
}

func TestPowAVX1(t *testing.T) {
	proc := PowProcs
	PowProcs = 1
	sp := testPowAVX(t)
	t.Logf("%d kH/sec on AVX PoW", int(sp))
	PowProcs = proc
}

