// +build amd64

package gadk

import (
	"testing"
	"time"
)

func testPowSSE(t *testing.T) float64 {
	var tx Trytes = ""
	s := time.Now()
	nonce, err := PowSSE(tx, 15)
	ti := time.Since(s)
	if err != nil {
		t.Fatal(err)
	}
	tx = tx[:len(tx)-NonceTrinarySize/3] + nonce
	h := tx.Hash()
	if h[len(h)-5:] != "99999" {
		t.Error("pow is illegal", h)
	}
	return float64(countSSE) / 1000 / ti.Seconds()
}

func TestPowSSE(t *testing.T) {
	sp := testPowSSE(t)
	t.Logf("%d kH/sec on SSE PoW", int(sp))
}
